import { Component } from "@angular/core";
import { Post } from "../post.model";
import {NgForm} from "@angular/forms"
import { PostService } from "../posts.service";

@Component({
  selector: 'app-post-create',   // helps us to use the component below, this is our own HTML tag
  templateUrl: './post-create.component.html',
  styleUrls: ['./post-create.component.css']
})

export class PostCreateComponent {
  enteredTitle='';
  enteredContent='';
  post: Post;
  private mode = 'create';
  private postId = 'string'

  constructor(public postServices: PostService){}

  onAddPost(form: NgForm){                          //form is of type NgForm
    if(form.invalid){
      return
    }
    this.postServices .addPost(form.value.title, form.value.content);
    form.resetForm();
  }
}
<mat-card>

  <form (submit)="onAddPost(postForm)" #postForm="ngForm">

    <mat-form-field>
      <input matInput type="text" name="title" ngModel required minLength="3" #title="ngModel" placeholder="Post Title">
      <mat-error *ngIf="title.invalid">Please Enter The Post Title</mat-error>
    </mat-form-field>

    <mat-form-field>
      <textarea matInput rows="6" name="content" ngModel required #content="ngModel" placeholder="Post Content"></textarea>
      <mat-error *ngIf="content.invalid">Please Enter The Post Content</mat-error>

    <hr>
    </mat-form-field>

    <button color="primary" mat-raised-button type="submit">Save Post</button>

  </form>
</mat-card>
