import { Component, EventEmitter, Output } from "@angular/core";

@Component({
  selector: 'app-post-create',   // helps us to use the component below, this is our own HTML tag
  templateUrl: './post-create.component.html',
  styleUrls: ['./post-create.component.css']
})

export class PostCreateComponent {
  enteredTitle='';
  enteredContent='';
  @Output() postCreated = new EventEmitter();

  onAddPost(){
    const post = {
      title: this.enteredTitle,
      content: this.enteredContent
    };

    this.postCreated.emit(post);
  }
}
<mat-card>

  <mat-form-field>
    <input matInput type="text" [(ngModel)] = "enteredTitle">
  </mat-form-field>

  <mat-form-field>
    <textarea matInput rows="6" [(ngModel)] = "enteredContent"></textarea>
  <hr>
  </mat-form-field>

  <button color="primary" mat-raised-button class="signUpButton" (click)="onAddPost()">Save Post</button>
</mat-card>
