import { Component,OnDestroy,OnInit } from "@angular/core";
import { Subscription } from 'rxjs';

import { Post } from "../post.model";
import { PostService } from "../posts.service";

@Component ({
  selector: 'app-post-list',
  templateUrl: './post-list.component.html',
  styleUrls: ['./post-list-component.css']
})

export class PostListComponent implements OnInit, OnDestroy {
  posts: Post[] = [];
  private postsSub: Subscription;

  constructor(public postService: PostService){ }

  ngOnInit(): void {
    this.postService.getPosts();
    this.postsSub = this.postService.getPostUpdateListener().subscribe((posts:Post[]) =>{     // .subscribe is part of the rxjs library
      this.posts = posts;
    });
  }

  ngOnDestroy(): void {
      this.postsSub.unsubscribe();
  }

}

<mat-accordion multi="true" *ngIf="posts.length >0">
  <mat-expansion-panel *ngFor="let post of posts">
    <mat-expansion-panel-header>
      {{post.title}}
    </mat-expansion-panel-header>
    <p>{{post.content}}</p>
    <mat-action-row>
      <button mat-button color="primary">EDIT</button>
      <button mat-button color="warn" (click)="onDelete" >DELETE</button>
    </mat-action-row>
  </mat-expansion-panel>
</mat-accordion>

<p class="info-text mat-body-1" *ngIf="posts.length<1">No Post Added Yet</p>
